class Solution {
public:
 bool canConstruct(const vector<int>& a, int x) {

    vector<bool> dp(x + 1, false);
    dp[0] = true;  


    for (int num : a) {

        for (int s = x; s >= num; --s) {
            if (dp[s - num]) {
                dp[s] = true;
            }
        }
    }

    return dp[x];
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q){
        int n = nums.size(),m = q.size(),res = -1;
        for(int i=0;i<n;++i){
            if(!nums[i]){
                res = max(res,0);
                continue;
            }
            vector<int> mp;
            int cres = -1;
            for(int j=0;j<m;++j){
                if(q[j][0] <= i and i <= q[j][1]){
                    if(q[j][2] > nums[i]) continue;
                    mp.push_back(q[j][2]);
                    if(canConstruct(mp,nums[i])){
                        cres = max(cres,j+1);
                        break;
                    }
                }
            }
            if(cres == -1) return cres;
            res = max(res,cres);
        }
        return res;
    }
};

