class Solution {
public:

    int solve(vector<vector<int>> &psum,int n,int col,int index,int sum,vector<vector<int>> &dp)
    {
        if(index>=n)
        {
            if(sum==0)
            {
                return index;
            }
            return 1e9;
        }

        if(sum==0)
        {
            return index;
        }

        if(sum<0)
        {
            return 1e9;
        }

        if(dp[index][sum]>-1)
        {
            return dp[index][sum];
        }

        int a = 1e9;
        if(sum-psum[index][col]>=0)
        {
            a = solve(psum,n,col,index+1,sum-psum[index][col],dp);
        }

        int b = solve(psum,n,col,index+1,sum,dp);

        dp[index][sum] = min(a,b);
        return dp[index][sum];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        vector<vector<int>> psum(m,vector<int>(n+1,0));

        for(int i=0;i<m;i++)
        {
            int a = queries[i][0];
            int b = queries[i][1];
            int c = queries[i][2];

            psum[i][a]+=c;
            psum[i][b+1]+=(-1*c);
        }

        for(int i=0;i<m;i++)
        {
            for(int j=1;j<n+1;j++)
            {
                psum[i][j] = psum[i][j] + psum[i][j-1];
            }
        }

        int ans = INT_MIN;
        for(int i=0;i<n;i++)
        {
            vector<vector<int>> dp(m+1,vector<int>(nums[i]+1,-1));
            int a = solve(psum,m,i,0,nums[i],dp);
            if(a==1e9)
            {
                return -1;
            }
            ans=max(ans,a);
        }


        return ans;
    }
};

