var minZeroArray = function (a, q) {
        let n = a.length;

        let f = true;
        for (let i = 0; i < n; i++) {
                if (a[i] !== 0) {
                        f = false;
                        break;
                }
        }
        if (f) return 0;

        let o = new Array(n);
        for (let i = 0; i < n; i++) {
                o[i] = [];
        }

        for (let i = 0; i < q.length; i++) {
                let s = q[i][0],
                        e = q[i][1],
                        d = q[i][2];
                for (let j = s; j <= e; j++) {
                        o[j].push([i, d]);
                }
        }

        let l = 1,
                r = q.length,
                res = -1;

        while (l <= r) {
                let m = ~~((l + r) / 2);
                if (c(a, o, m)) {
                        res = m;
                        r = m - 1;
                } else {
                        l = m + 1;
                }
        }

        return res;
};

function c(a, o, k) {
        let n = a.length;

        for (let i = 0; i < n; i++) {
                let t = a[i];
                let d = new Array(t + 1).fill(false);
                d[0] = true;

                for (let j = 0; j < o[i].length; j++) {
                        let p = o[i][j];
                        if (p[0] < k) {
                                let v = p[1];
                                for (let x = t; x >= v; x--) {
                                        if (d[x - v]) {
                                                d[x] = true;
                                        }
                                }
                        }
                }
                if (!d[t]) return false;
        }

        return true;
}

