class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        boolean allZero = true;
        for (int x : nums) {
            if (x != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;
        List<List<int[]>> coinsForIndex = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            coinsForIndex.add(new ArrayList<>());
        }
        for (int qi = 0; qi < m; qi++) {
            int l = queries[qi][0], r = queries[qi][1], v = queries[qi][2];
            for (int i = l; i <= r; i++) {
                coinsForIndex.get(i).add(new int[]{qi, v});
            }
        }
        int lo = 0, hi = m, ans = -1;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            boolean ok = true;
            for (int i = 0; i < n; i++) {
                List<int[]> coinList = coinsForIndex.get(i);
                int total = 0;
                List<Integer> available = new ArrayList<>();
                for (int[] pair : coinList) {
                    if (pair[0] < mid) {
                        available.add(pair[1]);
                        total += pair[1];
                    }
                }
                if (total < nums[i] || (nums[i] > 0 && !subsetSum(available, nums[i]))) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
    private boolean subsetSum(List<Integer> coins, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int coin : coins) {
            for (int j = target; j >= coin; j--) {
                if (dp[j - coin]) dp[j] = true;
            }
            if (dp[target]) return true;
        }
        return dp[target];
    }
}

