        n=len(nums)
        possible=[1<<x for x in nums]
        c=sum(x&1 for x in possible)
        if c==n:
            return 0
        for j,(l,r,val) in enumerate(queries):
            for i in range(l,r+1):
                if possible[i]&1:
                    continue
                possible[i]=possible[i]|(possible[i]>>val)
                if possible[i]&1:
                    c+=1
            if c==n:
                return j+1
        return -1

