        for (int i = 0; i < Q.size(); ++i) {
            int l = Q[i][0], r = Q[i][1], val = Q[i][2];
            for (int j = l; j <= r; ++j) {
                dp[j] = (dp[j] << val) | dp[j]; // decrement or not
            }
            bool ok = true;
            for (int j = 0; j < n; ++j) {
                if (!dp[j][A[j]]) {
                    ok = false;
                    break;
                }
            }
            if (ok) return i + 1;
        }
        return -1;
    }
};

