class Solution {
public:
    bool canMakeSum(vector<int>& arr, int target) {
        unordered_set<int> sums;
        sums.insert(0);
        for (int num : arr) {
            unordered_set<int> newSums;
            for (int s : sums) {
                if (s + num == target)
                    return true;
                newSums.insert(s + num);
            }
            sums.insert(newSums.begin(), newSums.end());
        }
        return sums.find(target) != sums.end();
    }
    bool isPossible(int mid,vector<int>& nums,vector<vector<int>>& queries){
        int n = nums.size();
        vector<vector<int>> vec(n);
        for(int i=0;i<mid;i++){
            int L = queries[i][0], R = queries[i][1], V = queries[i][2];
            for(int j=L;j<=R;j++){
                vec[j].push_back(V);
            }
        }
        for(int i=0;i<n;i++){
            if(!canMakeSum(vec[i],nums[i]))
                return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low=0,high=queries.size(),ans=-1;
        while(low<=high){
            int mid=(low+high)/2;
            if(isPossible(mid,nums,queries)){
                ans=mid;
                high=mid-1;
            }else{
                low=mid+1;
            }
        }
        return ans;
    }
};

