class Solution:
    def getSubsetSum(self, values, target):
        possible_sums = {0}
        for val in values:
            if target in possible_sums:
                return True
            new_sums = set(possible_sums)
            for s in possible_sums:
                new_sum = s + val
                if new_sum == target:
                    return True
                if new_sum < target:
                    new_sums.add(new_sum)
            possible_sums = new_sums

        return (target in possible_sums)

    def isPossible(self, nums, queries, k):
        n = len(nums)
        coverage = [[] for _ in range(n)]
        for i in range(k):
            l, r, val = queries[i]
            for idx in range(l, r+1):
                coverage[idx].append(val)
        for i in range(n):
            if nums[i] < 0:
                return False
            if sum(coverage[i]) < nums[i]:
                return False
            if not self.getSubsetSum(coverage[i], nums[i]):
                return False
        return True

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        left, right = 0, len(queries)
        res = -1

        while left <= right:
            mid = (left + right) // 2
            if self.isPossible(nums, queries, mid):
                res = mid
                right = mid - 1
            else:
                left = mid + 1

        return res if res != -1 else -1

