            opt1 = solve(num, i+1,sum-num[i],memo);
        }
        int opt2 = solve(num,i+1,sum,memo);
        return memo[i][sum] =opt1||opt2;
    }
bool makeSum(vector<int>& num, int sum) {
    int n = num.size();
    vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));

    // Initialize the dp array
    for (int i = 0; i <= n; ++i) {
        dp[i][0] = true; // Sum 0 can always be achieved with an empty subset
    }

    // Fill the dp table
    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= sum; ++j) {
            if (j < num[i - 1]) {
                dp[i][j] = dp[i - 1][j];
            } else {
                dp[i][j] = dp[i - 1][j] || dp[i - 1][j - num[i - 1]];
            }
        }
    }

    return dp[n][sum];
}
