from sortedcontainers import SortedList

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if max(nums) == 0:
            return 0
        left = 0
        right = len(queries) - 1
        n = len(nums)
        while left <= right:
            mid = (left + right) // 2
            curr_queries = queries[:mid+1]
            curr_queries.sort(key=lambda x: (x[0], x[1]))
            pq = []
            iq = 0
            isGood = True
            curr_options = SortedList()
            for idx in range(n):
                while pq and pq[0][0] < idx:
                    curr_options.remove(pq[0][1])
                    heapq.heappop(pq)
                while iq <= mid and idx >= curr_queries[iq][0]:
                    curr_options.add(curr_queries[iq][2])
                    heapq.heappush(pq, [curr_queries[iq][1], curr_queries[iq][2]])
                    iq += 1
                n_options = len(curr_options)
                n_num = nums[idx]
                if n_num == 0:
                    continue
                dp = [[False for _ in range(n_num + 1)] for _ in range(n_options + 1)]
                dp[0][0] = True
                for i in range(1, n_options + 1):
                    for j in range(n_num + 1):
                        # Use the current option
                        if j >= curr_options[i - 1]:
                            dp[i][j] = dp[i-1][j-curr_options[i - 1]]
                        # Don't use the current option
                        dp[i][j] |= dp[i-1][j]
                isGood = dp[-1][-1]
                if not isGood:
                    break
            if not isGood:
                if left == right:
                    break
                left = mid + 1
            else:
                if left == right:
                    break
                right = mid
        return left + 1 if isGood else -1

