class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)        
        zero_idx = set()
        dec = [set() for _ in range(n)]

        for i in range(n):
            if nums[i] == 0:
                zero_idx.add(i)
            else:
                dec[i].add(nums[i])

        if len(zero_idx) == n: 
            return 0

        for i, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                if j in zero_idx:
                    continue
                if v in dec[j]:
                    zero_idx.add(j)
                else:
                    for k in list(dec[j]):
                        if v == k:
                            zero_idx.add(j)
                        elif k > v:
                            dec[j].add(k - v)

            if len(zero_idx) == n:
                return i + 1

        return -1

