    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:    
        res = 0
        vals = [[] for _ in range(len(nums))]

        for i in range(len(nums)):
            while not self.make_num(nums[i], sorted(vals[i], reverse=True)):
                # include a new query
                while True:
                    res += 1

                    if res > len(queries):
                        return -1

                    l, r, val = queries[res - 1]

                    for j in range(max(l, i), r + 1):
                        vals[j].append(val)
                    if i >= l and i <= r:
                        break

        return res

    def make_num(self, num, values):

        @cache
        def backtrack(i, curr):
            if i == len(values):
                return curr == num

            if curr == num:
                return True

            if curr + values[i] <= num:
                if backtrack(i + 1, curr + values[i]):
                    return True
            if backtrack(i + 1, curr):
                return True
            return False

        return backtrack(0, 0)


