class Solution {
    int cache[1001][1001];
    int dfs(std::vector<int>& values, int idx, int target)
    {
        if (target == 0)
        {
            return idx;
        }

        if (idx >= values.size() || target < 0)
        {
            return INT_MAX;
        }

        // std::cout << "idx: " << idx << ", target: " << target << "n";
        if (cache[idx][target] != -1)
            return cache[idx][target];

        int steps = INT_MAX;

        // take
        steps = std::min(steps, dfs(values, idx + 1, target - values[idx]));

        // not take
        steps = std::min(steps, dfs(values, idx + 1, target));

        cache[idx][target] = steps;
        return steps;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        std::vector<std::vector<int>> digit_values(nums.size());

        for (auto& q : queries)
        {
            for (int i = 0; i < digit_values.size(); i++)
            {
                if (i >= q[0] && i <= q[1])
                {
                    digit_values[i].push_back(q[2]);
                }
                else
                {
                    digit_values[i].push_back(0);
                }
            }
        }

        int ret = -1;

        for (int i = 0; i < nums.size(); i++)
        {
            int target = nums[i];
            auto& values = digit_values[i];

            memset(cache, -1, sizeof(cache));
            int min_step = dfs(values, 0, target);

            if (min_step == INT_MAX)
                return -1;

            ret = std::max(ret, min_step);
        }

        return ret;
    }
};

