class Solution {
public:

    int tell(int index, int val, vector<pair<int, int>> &query, int prev, vector<vector<int>> &dp){
        if(val == 0) return prev + 1;
        if(index >= query.size()) return INT_MAX;
        if(dp[index][val] != -1) return dp[index][val];
        int ans = tell(index + 1, val, query, query[index].first, dp);
        if(query[index].second <= val){
            ans = min(ans, tell(index + 1, val - query[index].second, query, query[index].first, dp));
        }

        return dp[index][val] = ans;
    }


