            eachElement[i] = tell(0, nums[i], query, -1, dp);
        }
        int ans = 0;
        for(int i = 0; i<nums.size(); i++){
            if(eachElement[i] == INT_MAX) return -1;
            else{
                ans = max(ans, eachElement[i]);
            }
        }
        return ans;   
    }
};

