public class Solution {
    boolean dp[][];
    boolean verify(boolean dp[][], int nums[]){
        boolean check = true;
        for (int j = 0; j < nums.length; j++) {
            if (!dp[j][nums[j]]) {
                return false;
            }
        }
        return check;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        dp = new boolean[n][1001];
        for (int j = 0; j < n; j++) {
            dp[j][0] = true;
        }

        boolean isZero = true;
        for (int i : nums) {
            if (i != 0) {
                isZero = false;
                break;
            }
        }
        if (isZero) return 0;

        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];

            for (int j = l; j<=r; j++) {
                boolean[] res = new boolean[1001];
                for (int m = 0; m<1001; m++) {
                    if (dp[j][m] && m+v < 1001) {
                        res[m+v] = true;
                    }
                }
                for (int m = 0; m<1001; m++) {
                    if (res[m]) {
                        dp[j][m] = true;
                    }
                }
                for (int m = nums[j]+1; m<1001; m++) {
                    dp[j][m] = false;
                }
            }
            if (verify(dp, nums)) return i+1;
        }

        return -1;
    }
}

