class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int overall_max_j = -1;
        const int INF = m + 1;

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) continue;

            vector<int> v;
            for (int j = 0; j < m; j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    v.push_back(j);
                }
            }
            sort(v.begin(), v.end());

            vector<int> dp(target + 1, INF);
            dp[0] = -1;  

            for (int idx : v) {
                int val = queries[idx][2];
                for (int s = target - val; s >= 0; s--) {
                    if (dp[s] != INF && s + val <= target) {
                        dp[s + val] = min(dp[s + val], idx);
                    }
                }
            }

            if (dp[target] == INF) return -1;

            overall_max_j = max(overall_max_j, dp[target]);
        }

        return overall_max_j == -1 ? 0 : overall_max_j + 1;
    }
};

