class Solution {
public:
    int isSubsetSum(vector<pair<int,int>> &arr, int sum) {
        int n = arr.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));
        for (int i = 0; i <= n; i++) dp[i][0] = true;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= sum; j++) {
                if (j < arr[i - 1].first) {
                    dp[i][j] = dp[i - 1][j]; 
                }
                else {
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr[i - 1].first];
                }
            }
        }
        for(int i = 1; i<=n; i++){
            if(dp[i][sum]){
                return i-1;
            }
        }
        return -1;
    }
    int f(int ind, vector<pair<int,int>> &arr, int tar, int last, vector<vector<int>>& dp){
        if(tar == 0){
            return last;
        }
        if(arr.size() == ind || tar < 0 ){
            return -1;
        }

        int &may = dp[ind][tar];
        if(may != -2){
            return may;
        }

        int take = f(ind+1, arr, tar-arr[ind].first, ind, dp);
        if(take != -1){
            return may = take;
        }
        int notTake = f(ind+1, arr, tar, last, dp);
        if(notTake != -1){
            return may = notTake;
        }
        return may = -1;
    }
    int g(vector<pair<int, int>> &arr, int tar){
        int N = arr.size();

        vector<vector<int>> dp(N, vector<int>(tar+1, -2));
        return f(0, arr, tar, -1, dp);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int N = nums.size(), Q  = queries.size();
        vector<vector<pair<int,int>>> options(N);

        for(int i = 0; i<Q;  i++){
            for(int j = queries[i][0]; j<=queries[i][1]; j++){
                options[j].push_back({queries[i][2], i});
            }
        }

        int ans = 0;
        for(int i = 0; i<N; i++){
            if(nums[i] == 0)
                continue;

            int ret = isSubsetSum(options[i], nums[i]);
            if(ret == -1){
                return -1;
            }
            ans = max(ans, options[i][ret].second+1);
        }
        return ans;
    }
};

