class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();

        auto zero = [&](int k) -> bool {
            for (int j = 0; j < n; j++) {
                vector<bool> dp(1001, false);
                dp[0] = true;
                for (int i = 0; i < k; i++) {
                    if (j >= queries[i][0] && j <= queries[i][1]) {
                        for (int s = 1000 - queries[i][2]; s >= 0; s--) {
                            if (dp[s]) dp[s + queries[i][2]] = true;
                        }
                    }
                }
                if (!dp[nums[j]]) return false;
            }
            return true;
        };

        int low = 0, high = m, answer = -1;
        while (low <= high) {
            int mid = (low + high) / 2;
            if (zero(mid)) {
                answer = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return answer;
    }
};

