class Solution(object):
    def minZeroArray(self, nums, queries):
        """
        :type nums: List[int]
        :type queries: List[List[int]]
        :rtype: int
        """
        n = len(nums)
        if sum(nums) == 0: return 0
        dp = [set() for _ in range(n)]

        for j in range(n):
            dp[j].add(0)

        for k, (l, r, val) in enumerate(queries):
            for j in range(l, r+1):
                new_dp = {x+val for x in dp[j]}
                dp[j].update(new_dp)
                dp[j] = {x for x in dp[j] if x <= nums[j]}

            allDone = all(nums[j] in dp[j] for j in range(n))

            if allDone: return k + 1
        return -1

