class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def can_make(cnt, target):
            if not target:
                return True

            for k in cnt.keys():
                if not cnt[k] or target < k:
                    continue

                cnt[k] -= 1
                if can_make(cnt, target - k):
                    return True
                cnt[k] += 1

            return False

        n = len(nums)
        c = sum(num == 0 for num in nums)
        if n == c:
            return 0

        d = defaultdict(Counter)
        t = defaultdict(int)
        for res, [l, r, v] in enumerate(queries, 1):
            for i in range(l, r + 1):
                if not nums[i] or nums[i] < v:
                    continue

                d[i][v] += 1
                t[i] += v

                if t[i] == nums[i] or nums[i] == v or nums[i] < t[i] and can_make(d[i], t[i] - nums[i]):
                    nums[i] = 0
                    c += 1

                if n == c:
                    return res

        return -1

