class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n = len(nums)
        q_len = len(queries)

        left, right = 0, q_len
        answer = -1

        def can_make_zero_array(k: int) -> bool:

            for idx, original_val in enumerate(nums):
                if original_val == 0:
                    continue


                vals = []
                for q_idx, (l, r, v) in enumerate(queries):
                    if q_idx + 1 > k:
                        break
                    if l <= idx <= r:
                        vals.append(v)


                dp = [[False] * (original_val + 1) for _ in range(len(vals) + 1)]
                dp[0][0] = True  

                for i in range(1, len(vals) + 1):
                    val_i = vals[i - 1]  
                    for s in range(original_val + 1):
                        dp[i][s] = dp[i][s] or dp[i - 1][s]
                        if s >= val_i:
                            dp[i][s] = dp[i][s] or dp[i - 1][s - val_i]

                if not dp[len(vals)][original_val]:
                    return False

            return True

        while left <= right:
            mid = (left + right) // 2
            if can_make_zero_array(mid):
                answer = mid 
                right = mid - 1
            else:
                left = mid + 1

        if answer == -1:
            return -1
        if answer > q_len:
            return -1
        return answer

