class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        maximum = max(nums)
        if maximum == 0:
            return 0

        n = len(nums)
        m = len(queries)
        dp = [[False] * (maximum + 1) for _ in range(n)]

        for i in range(n):
            dp[i][nums[i]] = True

        for k in range(m):
            success = True
            l = queries[k][0]
            r = queries[k][1]
            val = queries[k][2]

            for i in range(n):
                if dp[i][0]:
                    continue

                if i >= l and i <= r and val <= nums[i]:
                    if dp[i][val]:
                        dp[i][0] = True
                    else:
                        for j in range(maximum + 1):
                            if dp[i][j] and j - val >= 0:
                                dp[i][j-val] = True

                if not dp[i][0]:
                    success = False

            if success:
                return k + 1

        return -1

