class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<pair<int, int>>> cover(n);

        for (int j = 0; j < m; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; ++i) {
                if (i >= 0 && i < n) {
                    cover[i].emplace_back(j, val);
                }
            }
        }

        int max_k = 0;
        for (int i = 0; i < n; ++i) {
            int k_i;
            if (nums[i] == 0) {
                k_i = 0;
            } else {
                if (cover[i].empty()) {
                    return -1;
                }
                unordered_set<int> S;
                S.insert(0);
                k_i = -1;
                for (auto& q : cover[i]) {
                    int j = q.first;
                    int val = q.second;
                    unordered_set<int> new_S = S;
                    bool found = false;
                    for (int s : S) {
                        int new_s = s + val;
                        if (new_s == nums[i]) {
                            k_i = j + 1;
                            found = true;
                            break;
                        }
                        if (new_s < nums[i]) {
                            new_S.insert(new_s);
                        }
                    }
                    if (found) {
                        break;
                    }
                    S = move(new_S);
                }
                if (k_i == -1) {
                    return -1;
                }
            }
            if (k_i > max_k) {
                max_k = k_i;
            }
        }

        return max_k;
    }
};

