class Solution {
public:
    bool isSubsetSum(vector<int> arr, int sum) {
        int n = arr.size();
        vector<bool> prev(sum + 1, false), curr(sum + 1);

        // Mark prev[0] = true as it is true
          // to make sum = 0 using 0 elements
        prev[0] = true;

        // Fill the subset table in
          // bottom up manner
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= sum; j++) {
                if (j < arr[i - 1])
                    curr[j] = prev[j];
                else
                    curr[j] = (prev[j] || prev[j - arr[i - 1]]);
            }
            prev = curr;
        }
        return prev[sum];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int l = 0, r = queries.size();
        r++;

        int n = nums.size();

        while(l < r){

            int mid = (l + r) / 2;
            vector <int> sum(n);

            vector <vector<int>> opt(n);

            for(int i = 0; i < mid; i++){
                int val = queries[i][2];
                for(int x = queries[i][0]; x <= queries[i][1]; x++){
                    sum[x] += val;

                    opt[x].push_back(val);
                }
            }

            bool pos = true;

            for(int i = 0; i < n; i++){
                if(sum[i] < nums[i])pos = false;
            }

            if(pos){
                for(int i = 0; i < n; i++){
                    pos &= isSubsetSum(opt[i], nums[i]);
                }
            }

            if(pos)r = mid;
            else{
                l = mid + 1;
            }
        }

        if(l == queries.size() + 1)l = -1;

        return l;


    }
};

