class Solution {
public:
    vector<vector<int>> dp;

    bool solve(int target, int idx, vector<vector<int>> &que, int q, int k) {
        if (target == 0) return true;
        if (q >= que.size() || q > k) return false;

        if (dp[target][q] != -1) return dp[target][q];

        bool notPick = solve(target, idx, que, q + 1, k);
        bool pick = false;
        if (que[q][0] <= idx && que[q][1] >= idx && que[q][2] <= target) {
            pick = solve(target - que[q][2], idx, que, q + 1, k);
        }

        return dp[target][q] = pick || notPick;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int maxNum = *max_element(nums.begin(), nums.end());
        int n = nums.size(), m = queries.size();

        int low = 0, high = m, ans = -1;

        auto isPoss = [&](int k) {
            for (int i = 0; i < n; ++i) {
                if (nums[i] == 0) continue;
                dp.assign(maxNum + 1, vector<int>(m + 1, -1));
                if (!solve(nums[i], i, queries, 0, k)) return false;
            }
            return true;
        };

        if (all_of(nums.begin(), nums.end(), [](int num) { return num == 0; })) return 0;

        while (low <= high) {
            int mid = (low + high) / 2;
            if (isPoss(mid)) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        if (ans == -1) return -1;
        return ans + 1;
    }
};

