class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if(!isKPossible(nums, queries, queries.length))
            return -1;
        int left = 0, right = queries.length;
        while(left<right){
            int mid = (left+right)/2;
            if(isKPossible(nums, queries, mid))
                right = mid;
            else
                left = mid+1;
        }
        return left;
    }
    private boolean isKPossible(int[] nums,int[][] queries, int k){
        ArrayList<Integer>[] list = (ArrayList<Integer>[]) new ArrayList[nums.length];
        for(int i=0;i<nums.length;i++)
            list[i] = new ArrayList<>();
        for(int i=0;i<k;i++){
            applyQuery(list, queries[i]);
        }
        boolean flag = true;
        for(int i=0;i<nums.length;i++){
            if(!sumPossibleDP(list[i],nums[i])){
                flag = false;
                break;
            }
        }
        return flag;
    }
    private boolean sumPossibleDP(ArrayList<Integer> list, int target) {
        if (target == 0) return true;
        if (list.isEmpty()) return false;

        boolean[] dp = new boolean[target + 1];
        dp[0] = true; 

        for (int num : list) {
            for (int j = target; j >= num; j--) {
                dp[j] |= dp[j - num];
            }
        }
        return dp[target];
    }
    private void applyQuery(ArrayList<Integer>[] list,int[] query){
        for(int i=query[0];i<=query[1];i++){
            list[i].add(query[2]);
        }
    }
}

