        int n = nums.size();
        vector<bitset<1001>> dp(n, bitset<1001>().set(0));

        int nofz = count(nums.begin(), nums.end(), 0);
        if (nofz == n) return 0;

        for (int i = 0; i < queries.size(); i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];

            for (int j = l; j <= r; j++) {
                dp[j] |= (dp[j] << val);
                for (int pos = nums[j] + 1; pos < 1001; pos++)
                    dp[j].reset(pos);
            }

            if (checker(nums, dp)) return i + 1;
        }
        return -1;
    }
};

