class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        # bitmask
        def is_feasible(k):
            for i in range(n):
                dp = 1 
                for l, r, val in queries[:k]:
                    if l <= i <= r:
                        dp |= dp << val
                        dp &= (1 << (nums[i] + 1)) - 1
                if not (dp >> nums[i]) & 1:
                    return False
            return True

        s, e = 0, m
        ans = -1
        while s <= e:
            mid = (s + e) // 2
            if is_feasible(mid):
                ans = mid
                e = mid - 1
            else:
                s = mid + 1
        return ans

