class Solution {
public:
    int func(vector<pair<int,int>>& operations, int target) {
        // Initialize dp array - each element represents min operations to reach that value
        vector<int> dp(target + 1, INT_MAX);
        dp[target] = 0;  // Base case: no operations needed if starting at target

        // Process each operation
        for(int i = 0; i < operations.size(); i++) {
            int val = operations[i].first;   // Value to subtract
            int opIdx = operations[i].second; // Operation index

            // Update dp array from right to left to avoid reusing the same operation
            for(int j = 0; j <= target; j++) {
                if(dp[j] == INT_MAX) continue; // Skip unreachable states

                // After applying operation, we get j - val
                int nextVal = j - val;
                if(nextVal >= 0) {
                    dp[nextVal] = min(dp[nextVal], max(dp[j], opIdx));
                }
            }
        }

        return dp[0] == INT_MAX ? -1 : dp[0];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int answer = 0;

        // Build operation maps for each index
        unordered_map<int, vector<pair<int,int>>> indexToOperations;
        for(int i = 0; i < queries.size(); i++) {
            int left = queries[i][0];
            int right = queries[i][1];
            int value = queries[i][2];

            for(int j = left; j <= right; j++) {
                indexToOperations[j].push_back({value, i + 1});
            }
        }

        // For each array element, find minimum set of operations to make it zero or negative
        for(int i = 0; i < n; i++) {
            // If value is already non-positive, no operations needed
            if(nums[i] <= 0) continue;

            int result = func(indexToOperations[i], nums[i]);
            if(result == -1) return -1; // If any element can't be made non-positive
            answer = max(answer, result);
        }

        return answer;
    }
};

