        for (int i = 0; i < n; i++) {

            vector<int> available;
            for (int j = 0; j < k; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if (l <= i && i <= r) {
                    available.push_back(val);
                }
            }

            int target = nums[i];

            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int v : available) {
                for (int s = target; s >= v; s--) {
                    dp[s] = dp[s] || dp[s - v];
                }
            }
            if (!dp[target]) {
                return false; 
            }
        }
        return true; 
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size();
        int ans = -1;
        // Binary search for the minimum k for which we can get a zero array.
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (canZeroArray(nums, queries, mid)) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

