    def earliest_ending_subsequence(self, nums, target):
        n = len(nums)
        dp = {0: 0}

        for i in range(n):
            current_dp = dp.copy()

            for prev_sum, prev_last_index in dp.items():
                current_sum = prev_sum + nums[i]

                if current_sum not in current_dp or i < current_dp[current_sum]:
                    current_dp[current_sum] = i

            dp = current_dp

        if target in dp:
            return dp[target]
        else:
            return -1


    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        N = len(nums)
        dick = {i: [0] for i in range(N)}

        for l, r, val in queries:
            q = set(i for i in range(l, r + 1))

            for i in range(N):
                if i in q:
                    dick[i].append(val)
                else:
                    dick[i].append(0)

        if any(self.earliest_ending_subsequence(dick[i], num) == -1 for i, num in enumerate(nums)):
            return -1

        return max(self.earliest_ending_subsequence(dick[i], num) for i, num in enumerate(nums))

