class Solution {
    int int_max = Integer.MAX_VALUE;
    public int minZeroArray(int[] nums, int[][] q) {
        // limit -> 0 to N
        List<List<int[]>> list = new ArrayList<>();
        for(int i=0;i<nums.length;i++) list.add(new ArrayList<>());

        for(int i=0;i<q.length;i++){
            int s = q[i][0], e = q[i][1], w = q[i][2];
            for(int j=s;j<=e;j++) list.get(j).add(new int[]{w,i+1});
        }
        int result = -1;
        for(int i=0;i<nums.length;i++){
            if(nums[i]==0){
                result = Math.max(result,0);
                continue;
            }
            intialize(list.get(i).size(), nums[i]+1);
            int minK = rec(0,nums[i],list.get(i));
            //System.out.printf("mink=%d i=%dn",minK,i);
            if(minK == int_max) return -1;
            result = Math.max(result,minK);
        }
        return result;


    }
    int[][] mem;
    void intialize(int len,int num){
        mem = new int[len][num];
        for(int i=0;i<len;i++)
            Arrays.fill(mem[i],-1);
    }
    int rec(int i,int num,List<int[]> list){
        if(i==list.size()){
            return int_max;
        }
        if(mem[i][num]!=-1){
            return mem[i][num];
        }
        //pick
        int pick = int_max;
        if(num >= list.get(i)[0]){
            if(num == list.get(i)[0]){
                pick = list.get(i)[1];
            }
            else{
                pick = rec(i+1, num-list.get(i)[0],list);
            }
        }
        // not pick
        int notPick = rec(i+1,num,list);
        int result = Math.min(pick,notPick);
        //System.out.printf("i=%d nuum=%d list=%s res=%dn",i,num,list,result);
        return mem[i][num] = result;
    }


}

