class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int mn = *min_element(nums.begin(), nums.end());
        int mx = *max_element(nums.begin(), nums.end());
        if(mn == mx && mn == 0) return 0;
        int q = queries.size();
        function<int(int)> solve = [&](int i) {
            vector<bool> dp(nums[i] + 1);
            dp[0] = true;
            for(int j = 0; j < q; j++) {
                if(queries[j][0] <= i && i <= queries[j][1]) {
                    for(int v = nums[i]; v >= queries[j][2]; v--) {
                        dp[v] = dp[v] || dp[v - queries[j][2]];
                    }
                    if(dp[nums[i]]) return j + 1;
                }
            }
            return 0x3f3f3f3f;
        };
        int ans = 0;
        for(int i = 0; i < n; i++) {
            ans = max(solve(i), ans);
        }
        return ans > q? -1: ans;
    }
};

