class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = nums.size();
        bool ok = true;
        for (int i = 0; i < m; i++) {
            if (nums[i] != 0) {
                ok = false;
                break;
            }
        }
        if (ok) {
            return 0;
        }

        int n = queries.size();
        int mx = -1;
        for (int i = 0; i < m; i++) {
            int num = nums[i];
            if (num == 0) continue;

            vector<bool>L(1001, false);
            int cur = -1;
            L[num] = true;
            for (int j = 0; j < n; j++) {
                if (i < queries[j][0] || i > queries[j][1]) continue;
                if (L[queries[j][2]]) {
                    cur = max(cur, j + 1);
                    break;
                }
                for (int k = queries[j][2] + 1; k <= 1000; k++) {
                    if (L[k]) {
                        L[k - queries[j][2]] = true;
                    }
                }
            }
            if (cur == -1) {
                return -1;
            } 
            mx = max(mx, cur);
        }

        return mx != -1 ? mx : -1;
    }
};

