class Solution {
public:
    int minZeroArray(vector<int>& targets, vector<vector<int>>& ops) {
        int len = targets.size(), totalOps = ops.size();
        vector<vector<bool>> dp(len);
        for (int i = 0; i < len; ++i) {
            dp[i].resize(targets[i] + 1, false);
            dp[i][0] = true;
        }

        bool alreadyZero = true;
        for (int i = 0; i < len; ++i)
            if (!dp[i][targets[i]])
                alreadyZero = false;
        if (alreadyZero)
            return 0;

        for (int op = 0; op < totalOps; op++) {
            int L = ops[op][0], R = ops[op][1], dec = ops[op][2];
            for (int idx = L; idx <= R; idx++)
                for (int s = targets[idx]; s >= dec; s--)
                    if (dp[idx][s - dec])
                        dp[idx][s] = true;
            bool allReached = true;
            for (int i = 0; i < len; i++)
                if (!dp[i][targets[i]])
                    allReached = false;
            if (allReached)
                return op + 1;
        }
        return -1;
    }
};

