class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        vector<bitset<1001>> dp(n, bitset<1001>().set(0));

        if (all_of(a.begin(), a.end(), [](int x) { return x == 0; })) return 0;

        for (int k = 0; k < q.size(); k++) {
            applyOp(dp, a, q[k][0], q[k][1], q[k][2]);
            if (checkAll(dp, a)) return k + 1;
        }
        return -1;
    }

private:
    void applyOp(vector<bitset<1001>>& dp, vector<int>& a, int l, int r, int v) {
        for (int j = l; j <= r; j++) {
            bitset<1001> s;
            for (int i = 0; i < 1001 - v; i++) if (dp[j][i]) s.set(i + v);
            dp[j] |= s;
            for (int p = a[j] + 1; p < 1001; p++) dp[j].reset(p);
        }
    }

    bool checkAll(vector<bitset<1001>>& dp, vector<int>& a) {
        for (int j = 0; j < a.size(); j++) if (!dp[j][a[j]]) return false;
        return true;
    }
};

