class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m=queries.size(),n=nums.size();
        int l=0,h=m+1,ans=-1;

        while(l<h){
            int mid=l+(h-l)/2;
            bool ok=true;

            for(int j=0;j<n;j++){
                if(nums[j]==0) continue;
                vector<int> v;
                for(int i=0;i<mid;i++){
                    int l=queries[i][0],r=queries[i][1],k=queries[i][2];
                    if(l<=j and j<=r) v.push_back(k);
                }
                if(v.empty()){ 
                    ok=false;
                    break; 
                }

                sort(v.begin(),v.end());
                unordered_map<long long,bool> dp;

                function<bool(int, int)> rec = [&](int idx,int target)->bool{
                    if(target==0) return true;
                    if(idx>=v.size()) return false;
                    long long key=((long long)idx<<32)|(target);
                    if (dp.find(key)!=dp.end()) return dp[key];
                    bool res=rec(idx+1,target);
                    if(!res and v[idx]<=target)
                        res=rec(idx+1,target-v[idx]);
                    dp[key]=res;
                    return res;
                };

                if(!rec(0,nums[j])){
                    ok=false;
                    break;
                }
            }
            if(ok){
                ans=mid;
                h=mid;
            }else{
                l=mid+1;
            }
        }
        if(ans>m)  return -1;
        return ans;
    }
};

