class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int l = -1, r = queries.length + 1;
        while (r - l > 1) {
            int mid = l + (r - l) / 2;
            if (check(nums, queries, mid)) {
                r = mid;
            } else {
                l = mid;
            }
        }
        return r == queries.length + 1 ? -1 : r;
    }

    private boolean check(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        List<Integer>[] ids = new List[n];
        Arrays.setAll(ids, o -> new ArrayList<>());
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for (int j = l; j <= r; j++) {
                ids[j].add(v);
            }
        }
        for (int i = 0; i < n; i++) {
            List<Integer> vs = ids[i];
            int num = nums[i];
            boolean[] dp = new boolean[num + 1];
            dp[0] = true;
            for (int v : vs) {
                for (int j = num; j >= v; j--) {
                    dp[j] |= dp[j - v];
                }
                if (dp[num]) break;
            }
            if (!dp[num]) return false;
        }
        return true;
    }
}

