class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        boolean alreadyZero = true;
        for (int num : nums) {
            if (num != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero)
            return 0;

        int m = queries.length;
        int low = 1, high = m + 1;
        int answer = -1;
        while (low < high) {
            int mid = low + (high - low) / 2;
            if (canZero(nums, queries, mid)) {
                answer = mid;
                high = mid;
            } else {
                low = mid + 1;
            }
        }
        return answer;
    }

    private boolean canZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        for (int j = 0; j < n; j++) {
            int target = nums[j];
            List<Integer> available = new ArrayList<>();
            for (int i = 0; i < k; i++) {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                if (j >= l && j <= r) {
                    available.add(val);
                }
            }
            if (!canSum(available, target)) {
                return false;
            }
        }
        return true;
    }

    private boolean canSum(List<Integer> vals, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int v : vals) {
            for (int s = target; s >= 0; s--) {
                if (dp[s] && s + v <= target) {
                    dp[s + v] = true;
                }
            }
        }
        return dp[target];
    }
}

