class Solution {
    bool chk(int sum, vector<vector<int>>& toPut, int index, vector<vector<int>>& dp, int val){
        if(sum == 0)return true;
        if(sum < 0)return false;
        if(index >= toPut[val].size())return false;
        // cout << sum << " " << index << toPut[val].size() << dp.size() << " " << dp[index].size() << endl;
        if(dp[index][sum] != -1) return dp[index][sum];
        bool vk = chk(sum-toPut[val][index], toPut, index+1, dp, val);
        if(vk)return dp[index][sum] = true;
        bool val2 = chk(sum, toPut, index+1, dp, val);
        return dp[index][sum] = val2;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size(), ans = -1;
        int size = nums.size();
        while(low <= high){
            int mid = low + (high - low) / 2;

            vector<vector<int>> toPut(size);
            for(int i = 0; i < mid; i++){
                for(int j = queries[i][0]; j <= queries[i][1]; j++){
                    toPut[j].push_back(queries[i][2]);
                }
            }
            bool val = true;
            for(int i = 0; i < size; i++){
                vector<vector<int>> dp(toPut[i].size(), vector<int>(nums[i]+10, -1));
                bool cv = chk(nums[i], toPut, 0, dp, i);
                if(!cv){
                    val = false;
                    break;
                }
            }
            if(val){
                ans = mid, high = mid-1;
            }

            else low = mid+1;
        }
        return ans;
    }
};

