class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int ans = 0;
        for(int ind = 0 ; ind < n ; ind++ ) {
            vector<int> q(m, 0);
            for(int j = 0 ; j < m ; j++ ) {
                if(ind>=queries[j][0] && ind<=queries[j][1])
                    q[j] = queries[j][2];
            }
            if(nums[ind] == 0) continue;
            vector<vector<bool>> dp(nums[ind]+1, vector<bool>(m, false));
            if(q[0]<=nums[ind]) dp[q[0]][0] = true;
            for(int i = 0 ; i < m ; i++ ) dp[0][i] = true;
            for(int i = 1 ; i < m ; i++ ) {
                for(int j = 1 ; j <= nums[ind] ; j ++ ) {
                    dp[j][i] = dp[j][i-1];
                    if(j-q[i] >= 0) dp[j][i] = dp[j][i] || dp[j-q[i]][i-1];
                }
            }
            int minInd = -1;
            for(int i = 0 ; i < m ; i++ ) {
                if(dp[nums[ind]][i]){
                    minInd = i+1;
                    break;
                }
            }
            if(minInd >= 0)
                ans = max(ans, minInd);
            else return -1;
        }

        return ans;
    }
};

