class Solution {
public:
    bool canMakeZero(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>> validVals(n);

        // Collect valid decrements for each index in nums
        for (int q = 0; q < k; q++) {
            int l = queries[q][0], r = queries[q][1], val = queries[q][2];
            for (int i = l; i <= r; i++) {
                validVals[i].push_back(val);
            }
        }

        // Check if each nums[i] can be reduced to zero using subset sum
        for (int i = 0; i < n; i++) {
            if (nums[i] > 0 && !subsetSum(validVals[i], nums[i])) {
                return false;
            }
        }
        return true;
    }

    bool subsetSum(vector<int>& values, int target) {
        if (target == 0) return true;
        if (values.empty()) return false;

        int m = values.size();
        vector<bool> dp(target + 1, false);
        dp[0] = true; // Base case: sum of 0 is always achievable

        for (int val : values) {
            for (int j = target; j >= val; j--) {
                dp[j] = dp[j] || dp[j - val];
            }
        }
        return dp[target];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // Edge case: If nums is already zero
        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) {
            return 0;
        }

        int left = 1, right = queries.size(), ans = -1;

        while (left <= right) {
            int mid = (left + right) / 2;
            if (canMakeZero(nums, queries, mid)) {
                ans = mid;
                right = mid - 1; // Try for a smaller k
            } else {
                left = mid + 1;
            }
        }
        return ans;
    }
};

