class Solution { 
    public int minZeroArray(int[] nums, int[][] queries) {
        // -2 -3 3 2 -> -2 -5 -2 0
        // -1 -2 0 1 -2 -> -1 -3 -3 -2 -4
        int left = 0, right = queries.length;
        while (left + 1 < right) {
            int mid = left + (right - left) / 2;
            if (canMakeAllZero(nums, queries, mid)) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        // System.out.println("left is " + left + ", right is " + right);
        if (canMakeAllZero(nums, queries, left)) {
            return left;
        }
        if (canMakeAllZero(nums, queries, right)) {
            return right;
        }
        return -1;
    }

    // private boolean canMakeAllZero(int[] nums, int[][] queries, int k) {
    //     int[] diff = new int[nums.length + 1];
    //     for (int i = 0; i < k; i++) {
    //         int[] query = queries[i];
    //         diff[query[0]] -= query[2];
    //         diff[query[1] + 1] += query[2];
    //     }

    //     for (int i = 0; i < nums.length; i++) {
    //         if (i > 0) {
    //             diff[i] += diff[i - 1];
    //         }
    //         if (nums[i] + diff[i] > 0) {
    //             return false;
    //         }
    //     }
    //     return true;
    // }

    private boolean canMakeAllZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        List<List<Integer>> diffs = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            diffs.add(new ArrayList<>());
        }
        for (int i = 0; i < k; i++) {
            int[] query = queries[i];
            int leftIdx = query[0], rightIdx = query[1], d = query[2];
            for (int j = leftIdx; j <= rightIdx; j++) {
                diffs.get(j).add(d);
            }
        }

        for (int i = 0; i < nums.length; i++) {
            int num = nums[i];
            if (num == 0) {
                continue;
            }
            List<Integer> diff = diffs.get(i);
            // System.out.println(diff);
            // check if the sum of subsequence in diff arr can make up to num
            Boolean[][] memo = new Boolean[diff.size()][num + 1];
            if (!helper(diff, num, 0, 0, memo)) {
                return false;
            }
        }
        return true;
    }

    private boolean helper(List<Integer> arr, int sum, int idx, int curSum, Boolean[][] memo) {
        if (curSum == sum) {
            return true;
        }
        if (curSum > sum) {
            return false;
        }
        if (idx == arr.size()) {
            return curSum == sum;
        }
        if (memo[idx][curSum] != null) {
            return memo[idx][curSum];
        }
        boolean res = false;
        // skip the current idx
        res |= helper(arr, sum, idx + 1, curSum, memo);
        // use the current idx
        res |= helper(arr, sum, idx + 1, curSum + arr.get(idx), memo);
        return memo[idx][curSum] = res;
    }
}

