class Solution {
private:
    bool f(int idx, int target, vector<vector<int>>& dp, vector<int>& arr) {
        // BASE CASE
        if (target == 0)
            return true;
        if (idx == 0) {
            if (target == arr[idx])
                return true;
            else
                return false;
        }
        if (dp[idx][target] != -1)
            return dp[idx][target];
        bool not_pick = f(idx - 1, target, dp, arr);
        bool pick = false;
        if (arr[idx] <= target) {
            pick = f(idx - 1, target - arr[idx], dp,arr);
        }
        return dp[idx][target] = pick || not_pick;
    }

    bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size(), q = queries.size();
        for (int i = 0; i < n; i++) {
            if(nums[i]==0) continue;
            int ele = nums[i];
            vector<int> arr;
            for (int j = 0; j < k; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if (i >= l && i <= r) {
                    arr.push_back(queries[j][2]);
                }
            }
            if(arr.size()==0 && nums[i]!=0) return false;
            vector<vector<int>> dp(arr.size(), vector<int>(1001, -1));
            if (!(f(arr.size() - 1, ele, dp, arr))) {
                return false;
            }
        }
        return true;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size(), k = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (check(nums, queries, mid)) {
                k = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return k;
    }
};

