class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        boolean flag = true;
        for(int i=0;i<nums.length;i++){
            if(nums[i] != 0){
                flag = false;
                break;
            }
        }

        if(flag == true){
            return 0;
        }

        Map<Integer, List<Integer>> hash = new HashMap<>();
        for(int i=0;i<nums.length;i++){
            hash.put(i, new ArrayList<>());
        }

        int count = 0;
        for(int[] query : queries){
            int l = query[0];
            int r = query[1];
            int val = query[2];

            for(int i=l;i<=r;i++){
                List<Integer> list = hash.get(i);
                list.add(val);

                hash.put(i, list);
            }

            if(isPossible(nums, hash)){
                return count + 1;
            }

            count++;
        }

        return -1;
    }

    public boolean isPossible(int[] nums, Map<Integer, List<Integer>> hash){
        for(int i=0;i<nums.length;i++){
            int[][] dp = new int[hash.get(i).size() + 1][nums[i] + 1];
            for(int j=0;j<hash.get(i).size()+1;j++){
                Arrays.fill(dp[j], -1);
            }

            if(targetSum(hash.get(i), nums[i], 0, dp) == false){
                return false;
            }
        }

        return true;
    }

    public boolean targetSum(List<Integer> list, int target, int idx, int[][] dp){
        if(target == 0){
            return true;
        }

        if(idx == list.size()){
            return false;
        }

        if(dp[idx][target] != -1){
            return dp[idx][target] == 1 ? true : false;
        }

        if(list.get(idx) <= target){
            boolean ans = targetSum(list, target - list.get(idx), idx+1, dp);
            dp[idx][target] = ans ? 1 : 0;
            if(ans == true){
                return true;
            }
        }

        boolean ans = targetSum(list, target, idx+1, dp);
        dp[idx][target] = ans ? 1 : 0;
        if(ans == true){
            return true;
        }

        return false;
    }


}

