class Solution {
public:
    int isPossible(int target, int idx, vector<pair<int, int>>& vals, vector<unordered_map<int, int>>& ht) {
        // base
        if (idx >= vals.size())
            return -1;
        // cout << "At " << target << ", " << idx << ": " << endl;

        // choose idx or not;
        if (vals[idx].first == target) {
             // cout << "target " << target << " met at " << idx << endl;
            return idx;   
        }
        if (ht[target].find(idx) != ht[target].end())
            return ht[target][idx];
        int ans = -1;
        if (vals[idx].first < target) {
            // choose
            // cout << "choosing " << idx << endl;
            int v1 = isPossible(target - vals[idx].first, idx + 1, vals, ht);
            // cout << "At " << target << ", " << idx << ": v1 as " << v1 << endl;
            ans = v1;
        }
        // not choose
        int v2 = isPossible(target, idx + 1, vals, ht);
        // cout << "At " << target << ", " << idx << ": v2 as " << v2 << endl;
        if (ans == -1) ans = INT_MAX;
        if (v2 == -1) v2 = INT_MAX;
        ans = min(ans, v2);
        if (ans == INT_MAX) ans = -1;
        // cout << "target = " << target << ", at = " << idx << " => " << ans << endl;
        return ht[target][idx] = ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool alreadyZero = true;
        for (int i=0; i<nums.size(); ++i) {
            if (nums[i] != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero) return 0;
        bool ok = true;
        int k = -1;
        for (int i=0; i<nums.size(); ++i) {
            int num = nums[i];
            if (num == 0) continue;
            vector<pair<int, int>> data;
            for (int j=0; j<queries.size(); ++j) {
                auto& query = queries[j];
                if (query[0] <= i && query[1] >= i)
                    data.push_back({query[2], j});
            }
            vector<unordered_map<int, int>> ht(num+1, unordered_map<int, int>());
            int val = isPossible(num, 0, data, ht);
            if (val == -1) {
                ok = false;
                break;
            }
            int maxQ = data[val].second + 1;  // idx + 1
            // cout << i << " -> " << maxQ << endl;
            k = max(k, maxQ);
        }
        if (!ok) return -1;
        return k;
    }
};

