class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        cache = [[] for _ in range(n)]        
        for i in range(len(queries)):
            l, r, val = queries[i]
            upper = min(n-1, r)
            for idx in range(l, upper + 1):
                cache[idx].append(i)

        max_q_idx = -1

        for i, num in enumerate(nums):
            if num == 0:
                continue
            temp = cache[i]
            if not temp:
                return -1

            total_sum = 0
            max_idx = -1
            dp = {total_sum: max_idx}
            for j in temp:
                val = queries[j][2]
                new_entries = {}
                for s in dp:
                    curr_max = dp[s]
                    new_val = s + val
                    new_max = max(curr_max, j)
                    if not new_val in dp:
                        new_entries[new_val] = new_max
                    elif new_max < dp[new_val]:
                        new_entries[new_val] = min(new_max, dp[new_val])

                for s, new_max in new_entries.items():
                    if not s in dp or new_max < dp[s]:
                        dp[s] = new_max

            if not num in dp:
                return -1

            current_m = dp[num]
            max_q_idx = max(max_q_idx, current_m)

        if max_q_idx == -1:
            return 0
        else:
            return max_q_idx + 1

