class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),m=queries.size();
        vector<vector<bool>>vec(n,vector<bool>(1001,false));

        for(int i=0;i<n;i++){
            vec[i][nums[i]]=true;
        }
        for(int i=0;i<n;i++){
            if(nums[i]!=0)break;
            if(i==n-1)return 0;
        }
        for(int k=0;k<m;k++){
            bool b=true;
            for(int j=queries[k][0];j<=queries[k][1];j++){
                vector<bool>tmp=vec[j];
                for(int t=nums[j];t>=queries[k][2];t--){
                    if(tmp[t]){
                        vec[j][t-queries[k][2]]=true;
                    }
                }
            }
            for(int j=0;j<n;j++){
                if(!vec[j][0]){ 
                    b=false;
                    break;
                }
            }
            if(b)return k+1;
        }
        return -1;
    }
};

