class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        int n = nums.length;
        BitSet[] bt = new BitSet[n];

        boolean flag = false;

        for(int x: nums){
            if(x!=0){
                flag = true;
                break;
            }
        }

        if(!flag)return 0;

        for(int i=0;i<n;i++){

            bt[i] = new BitSet(1001);
            bt[i].set(0);
        }

        int idx =1;

        for(int[] q: queries){

            int a = q[0];
            int b = q[1];
            int v = q[2];
            boolean flaag = false;

            for(int i=a;i<=b;i++){

                BitSet temp = new BitSet(1001);

                sfunc(bt ,i, v, temp);

                bt[i].or(temp);
                bt[i].clear( nums[i]+1, 1001 );
            }

            for(int i=0;i<n;i++){

                if(!bt[i].get(nums[i])){
                    flaag = true;
                    break;
                }
            }

            if(!flaag)return idx;
            idx++;

        }

        return -1;
    }

    void sfunc(BitSet[] bt, int a, int v, BitSet temp){
        for(int i=bt[a].nextSetBit(0); i>=0; i = bt[a].nextSetBit(i+1)){

            if(i + v < 1001){
                temp.set(i + v);
            }
        }
    }
}

